mapper.select:
SELECT b.id, b.title, b.inserted, COUNT(r.id) numOfReply FROM Board b LEFT JOIN Reply r ON b.id = r.board_id
WHERE b.title LIKE #{keyword } OR b.body LIKE #{keyword }
GROUP BY b.id ORDER BY id DESC LIMIT #{from }, #{row }

mapper.update:
UPDATE Board
SET title = #{title},
	body = #{body}
WHERE id = #{id}
